import React from 'react';
import {PieChart, BarChart, Legend} from 'react-easy-chart';
import { Button, Grid, Row, Col} from 'react-bootstrap';
import ReactTable from 'react-table'
import '../node_modules/react-table/react-table.css'
import './App.css'
import ReactUSA from 'react-usa';
// import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
// import '../node_modules/react-bootstrap-table/dist/react-bootstrap-table-all.min.css';

export default class App extends React.Component {
  constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        items: [
          { key: 'A', value: 100 },
          { key: 'B', value: 200 },
          { key: 'C', value: 50 }
        ],
        bar: [
          {x: 'A', y:100},
          { x: 'B', y: 200 },
          { x: 'C', y: 50 }
        ],
        blacklist:[{'obscene':[]}],
        states: []
      };
      this.refresh = this.refresh.bind(this);
  }

  mapHandler = (event) => {
    alert(event.target.dataset.name);
  };

  statesCustomConfig = () => {
    return {
      "NJ": {
        fill: "navy",
        clickHandler: (event) => console.log('Custom handler for NJ', event.target.dataset)
      },
      "NY": {
        fill: "#CC0000"
      }
    };
  };

  refresh() {
    this.setState({
      isLoaded : false
    });
    fetch("http://localhost:5000/refresh")
        .then(res => res.json())
        .then(
          (result) => {
            console.log("res: " + result);
            this.setState({
              isLoaded: true,
              items: result[0],
              bar: result[1],
              blacklist: result[2],
              states: result[3]
            });
          },
          (error) => {
            this.setState({
              isLoaded: true,
              error: error
            });
          }
        )
  }
  componentDidMount() {
      this.refresh();
  }
  render () {
    const { error, isLoaded, items, bar, blacklist } = this.state;
    console.log(blacklist)
    const columns = [{
      Header: 'Name',
      accessor: 'name',
      width: 250, // String-based value accessors!
      height: 500
    }, {
      Header: 'Tweet',
      accessor:'tweet',
      width: 700,
      height: 500
    }];
    const customStyle = {
      '.legend': {
        margin: 10
      }
    };
    const mapboxAccessToken = "pk.eyJ1IjoiaGFsZmxrYWthIiwiYSI6ImNqcGs2Y3ppYjAwNDQzeHFsemR4dnN5MXMifQ.cMuKGmhr9-TD-6cILcRldA"
    const mapboxType = "streets";
    const position = [37.0902, -95.7129];
    const zoom = 4;
    const data = [
      {
        name: "Nebraska",
        values: [{label: "Capital", val: "Lincoln"}, {label: "Electoral Votes", val: 3}],
        color: "#E31A1C"
      }
    ]
    const stateStyle = { weight: 1, opacity: 1, color: '#666', dashArray: '3', fillOpacity: 0.7 };
    const stateHoverStyle = { weight: 5, color: '#FFF', dashArray: '1', fillOpacity: 0.7 };
    const excludeStates = ["District of Columbia", "Puerto Rico"];

    if (error) {
        return <div>Error: {error.message}</div>;
    } else {
      console.log("draw...");
      console.log(items);
      return (
        <div>

            <ReactUSA
              mapboxAccessToken={mapboxAccessToken} // Required
              mapHeight="800px" // Required
              mapWidth="100%"
              className="container"
              mapboxType={mapboxType}
              mapCenter={position}
              mapZoom={zoom}
              mapScrollZoom={false}
              neighborhoodOn={true}
              tooltip={true}
              tooltipSticky={false}
              data={data}
              stateStyle={stateStyle}
              stateHoverStyle={stateHoverStyle}
              excludeStates={excludeStates}
            />
      </div>
      );
    }
  }
}
